mapper.select:
SELECT id, name, description, cover, questions, cascades, status, is_delete, is_active, is_random, question_number, progress_bar, password, is_password, answer_sheet_visible, auto_save, enable_update, copy_enabled, tag_ids, answer_analysis, types, question_num, start_time, end_time FROM tb_project
is_delete = 0
and name like concat('%',#{condition.keywords},'%')
and status = #{condition.status}
ORDER BY id DESC LIMIT #{current},#{size}
