SELECT a.id, user_id, question_title, question_description, images, questions, type, a.is_delete, a.status, a.create_time, t.id AS tag_id, t.tag_name FROM ( SELECT id, user_id, question_title, question_description, images, questions, type, is_delete, status, create_time FROM tb_question
is_delete = #{condition.isDelete}
and question_title like concat('%',#{condition.keywords},'%')
and status = #{condition.status}
and type = #{condition.type}
and id in ( SELECT question_id FROM tb_question_tag WHERE tag_id = #{condition.tagId} )
ORDER BY id DESC LIMIT #{current},#{size} ) a LEFT JOIN tb_question_tag atg ON a.id = atg.question_id LEFT JOIN tb_tag t ON t.id = atg.tag_id ORDER BY a.id DESC

SELECT
count(DISTINCT a.id)
from
tb_question a
LEFT JOIN tb_question_tag tat on  a.id = tat.question_id

    is_delete = #{condition.isDelete}
    
        and question_title like concat('%',#{condition.keywords},'%')
    
    
